import React from "react";
import { useState } from "react";

const Form = ({ formData, setFormData }) => {
  [formData, setFormData] = useState({
    date: "",
    description: "",
    category: "",
    amount: 0,
  })
  const [searchInput, setSearchInput] = useState("");

  function handleChange(event) {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  }


  const saveGames = (e) => {
    e.preventDefault();
    fetch('http://localhost:3000/transactions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(
        formData
      ),
    })
  }

  const handleChanger = (e) => {
    e.preventDefault();
    setSearchInput(e.target.value);
  };

  if (searchInput.length > 0) {
    formData.filter((data) => {
      return data.name.match(searchInput);
    });
  }



  return (
    <>
      <form className="d-flex">
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" onChange={handleChanger}
          value={searchInput} ></input>
        <button className="btn btn-outline-success" type="submit">Search</button>
      </form>
      <form className="row p-3" onSubmit={saveGames}

      >
        <div className="col-3">
          <input
            name="date"
            onChange={handleChange}
            value={formData.date}
            className="form-control"
            placeholder="MM/DD/YYY"
          />
        </div>
        <div className="col-3">
          <input
            name="description"
            onChange={handleChange}
            value={formData.description}
            className="form-control"
            placeholder="Description"
          />
        </div>
        <div className="col-3">
          <input
            name="category"
            onChange={handleChange}
            value={formData.category}
            className="form-control"
            placeholder="Category"
          />
        </div>
        <div className="col-3">
          <input
            name="amount"
            onChange={handleChange}
            value={formData.amount}
            className="form-control"
            placeholder="Amount"
          />
        </div>

        <div className="d-grid gap-2">
          <button className="btn btn-primary" >SUBMIT</button>
        </div>
      </form>



    </>
  );
};
// };

export default Form;
